      "instructions": [
        {
          "start_time": 0,
          "appliance": null,
          "end_time": 0,
          "temperature": null,
          "id": 71623,
          "position": 1,
          "display_text": "Fill a medium pot halfway with canola oil. Heat over medium heat until the temperature reaches 375°F (190°C)."
        },
        {
          "id": 71624,
          "position": 2,
          "display_text": "Peel the plantain and cut lengthwise into 3 pieces.",
          "start_time": 0,
          "appliance": null,
          "end_time": 0,
          "temperature": null
        },
        {
          "start_time": 0,
          "appliance": null,
          "end_time": 0,
          "temperature": null,
          "id": 71625,
          "position": 3,
          "display_text": "Dip the plantain pieces in the brown sugar to coat evenly on all sides."
        },
        {
          "temperature": null,
          "id": 71626,
          "position": 4,
          "display_text": "Set a spring roll wrapper on a clean surface in a diamond orientation. Place a plantain strip horizontally across the center of the wrapper and top with a few slices of jackfruit, if using. Fold the bottom corner of the wrapper up and over the plantain, then fold in the sides. Brush the exposed edge with egg white, then roll to seal. Repeat with the remaining wrappers and plantains.",
          "start_time": 0,
          "appliance": null,
          "end_time": 0
        },
        {
          "appliance": null,
          "end_time": 0,
          "temperature": null,
          "id": 71627,
          "position": 5,
          "display_text": "Add the wrapped plantains to the hot oil and fry, flipping once, until the wrappers turn golden brown, 1–2 minutes total.",
          "start_time": 0
        },
        {
          "start_time": 0,
          "appliance": null,
          "end_time": 0,
          "temperature": null,
          "id": 71628,
          "position": 6,
          "display_text": "Serve the turon hot with more brown sugar sprinkled on top and/or a scoop of ice cream, if desired."
        },
        {
          "end_time": 0,
          "temperature": null,
          "id": 71629,
          "position": 7,
          "display_text": "Enjoy!",
          "start_time": 0,
          "appliance": null
        }
      ],
