      "instructions": [
        {
          "end_time": 0,
          "temperature": null,
          "id": 73294,
          "position": 1,
          "display_text": "Remove the chicken from the refrigerator 30 minutes prior cooking.",
          "start_time": 0,
          "appliance": null
        },
        {
          "start_time": 2000,
          "appliance": null,
          "end_time": 4166,
          "temperature": null,
          "id": 73295,
          "position": 2,
          "display_text": "Pat the chicken dry with a paper towel, then season both sides generously with salt, black pepper, and Italian seasoning. Dredge in the flour, shaking off any excess."
        },
        {
          "id": 73296,
          "position": 3,
          "display_text": "Heat a medium skillet over medium-high heat. Melt together the avocado oil and butter, then add the chicken in a single layer and cook for 3½ minutes on each side, or until golden and crispy. Remove the chicken from the skillet and set aside.",
          "start_time": 5000,
          "appliance": null,
          "end_time": 8966,
          "temperature": null
        },
        {
          "id": 73297,
          "position": 4,
          "display_text": "Add the shallot and garlic to the skillet and cook for 3 minutes, being careful to not let them brown. Add the thyme and red pepper flakes and stir for 1 minute, until fragrant.",
          "start_time": 9633,
          "appliance": null,
          "end_time": 10966,
          "temperature": null
        },
        {
          "display_text": "Add the chicken broth, heavy cream, and sun-dried tomatoes and cook until the liquid reduces slightly, 3–5 minutes.",
          "start_time": 11799,
          "appliance": null,
          "end_time": 13933,
          "temperature": null,
          "id": 73298,
          "position": 5
        },
        {
          "display_text": "Add the Parmesan cheese and stir until the sauce thickens slightly. Season the sauce with salt and black pepper to taste.",
          "start_time": 14766,
          "appliance": null,
          "end_time": 15433,
          "temperature": null,
          "id": 73299,
          "position": 6
        },
        {
          "end_time": 17900,
          "temperature": null,
          "id": 73300,
          "position": 7,
          "display_text": "Return the chicken to the skillet and cook for 2 minutes, or until warmed through.",
          "start_time": 16099,
          "appliance": null
        },
        {
          "id": 73301,
          "position": 8,
          "display_text": "Garnish with the basil and serve with mashed potatoes or pasta.",
          "start_time": 0,
          "appliance": null,
          "end_time": 0,
          "temperature": null
        },
        {
          "end_time": 22666,
          "temperature": null,
          "id": 73302,
          "position": 9,
          "display_text": "Enjoy!",
          "start_time": 19333,
          "appliance": null
        }
      ],
