      "instructions": [
        {
          "id": 72078,
          "position": 1,
          "display_text": "Heat the avocado oil in a large diameter wok or skillet over medium-high heat until the temperature reaches 350°F (180°C).",
          "start_time": 0,
          "appliance": null,
          "end_time": 0,
          "temperature": null
        },
        {
          "id": 72079,
          "position": 2,
          "display_text": "Carefully add the bananas to the hot oil, then sprinkle the brown sugar over the bananas. Fry for 2 minutes without stirring to allow the sugar to caramelize. Carefully turn bananas with tongs or a wok spatula to coat with the caramelized sugar. Continue frying, turning frequently, until the bananas are deep golden brown in color, 3–5 minutes.",
          "start_time": 0,
          "appliance": null,
          "end_time": 0,
          "temperature": null
        },
        {
          "position": 3,
          "display_text": "Remove the bananas from the oil and place on parchment paper and let sit until cool enough to touch, about 5 minutes.",
          "start_time": 0,
          "appliance": null,
          "end_time": 0,
          "temperature": null,
          "id": 72080
        },
        {
          "start_time": 0,
          "appliance": null,
          "end_time": 0,
          "temperature": null,
          "id": 72081,
          "position": 4,
          "display_text": "Insert 1–2 skewers into the bottom of each banana cue. Serve immediately."
        }
      ],
