      "servings_noun_plural": "servings",
      "cook_time_minutes": 10,
      "instructions": [
        {
          "start_time": 0,
          "appliance": null,
          "end_time": 0,
          "temperature": null,
          "id": 71819,
          "position": 1,
          "display_text": "In a medium bowl mix together the kimchi fried rice and grated mozzarella until combined."
        },
        {
          "end_time": 0,
          "temperature": null,
          "id": 71820,
          "position": 2,
          "display_text": "Scoop 4 tablespoons of the fried rice  mixture onto a sheet of plastic wrap and flatten with the back of a spoon. Place a cube of mozzarella in the center, then wrap tightly in the plastic wrap shape the rice into a ball, completely encasing the cheese. Repeat with the remaining ingredients; you should have 8 rice balls total.",
          "start_time": 0,
          "appliance": null
        },
        {
          "start_time": 0,
          "appliance": null,
          "end_time": 0,
          "temperature": null,
          "id": 71821,
          "position": 3,
          "display_text": "Chill the rice balls in the refrigerator for at least 1 hour, up to overnight."
        },
        {
          "end_time": 0,
          "temperature": null,
          "id": 71822,
          "position": 4,
          "display_text": "Preheat the air fryer to 390°F (198°C).",
          "start_time": 0,
          "appliance": null
        },
        {
          "end_time": 0,
          "temperature": null,
          "id": 71823,
          "position": 5,
          "display_text": "Place 4 of the rice balls in the air fryer and cook for 10 minutes, until crispy and golden brown. Repeat with the remaining rice balls.",
          "start_time": 0,
          "appliance": null
        },
        {
          "start_time": 0,
          "appliance": null,
          "end_time": 0,
          "temperature": null,
          "id": 71824,
          "position": 6,
          "display_text": "Serve hot."
        },
        {
          "appliance": null,
          "end_time": 0,
          "temperature": null,
          "id": 71825,
          "position": 7,
          "display_text": "Enjoy!",
          "start_time": 0
        }
      ],
