      "video_url": "https://vid.tasty.co/output/176545/hls24_1597888165.m3u8",
      "instructions": [
        {
          "display_text": "Make the cotton candy: Let the machine heat for 10 minutes before using, then turn off.",
          "start_time": 0,
          "appliance": null,
          "end_time": 0,
          "temperature": null,
          "id": 58017,
          "position": 1
        },
        {
          "end_time": 250166,
          "temperature": null,
          "id": 58018,
          "position": 2,
          "display_text": "Add the pink hard candies to the machine, then turn back on. When you see the cotton candy web begin, hold a cone in the center at a 45° angle. Move the cone around the bowl and spin while the candy collects on the end, then move the cone into a horizontal position and continue rotating to collect the rest of the candy, moving from side to side.",
          "start_time": 230000,
          "appliance": null
        },
        {
          "temperature": null,
          "id": 58019,
          "position": 3,
          "display_text": "Carefully remove the cotton candy from the cone and press into a 3-inch (7.5 cm) round mold to compact.",
          "start_time": 252000,
          "appliance": null,
          "end_time": 256000
        },
        {
          "end_time": 262500,
          "temperature": null,
          "id": 58020,
          "position": 4,
          "display_text": "Let the machine heat up again before using, about 5 minutes, then repeat with the blue hard candies.",
          "start_time": 258000,
          "appliance": null
        },
        {
          "temperature": null,
          "id": 58021,
          "position": 5,
          "display_text": "To assemble, use a serrated knife to cut a 1-inch (2.54 cm) disc crosswise from the pint of ice cream. Peel off the carton.",
          "start_time": 275000,
          "appliance": null,
          "end_time": 281833
        },
        {
          "temperature": null,
          "id": 58022,
          "position": 6,
          "display_text": "Sandwich the ice cream round between the blue and pink cotton candy. Add the sprinkles to a shallow dish, then roll the edges of the ice cream sandwich in the sprinkles.",
          "start_time": 284000,
          "appliance": null,
          "end_time": 321333
        },
        {
          "appliance": null,
          "end_time": 0,
          "temperature": null,
          "id": 58023,
          "position": 7,
          "display_text": "Freeze for 30 minutes more before serving.",
          "start_time": 0
        },
        {
          "start_time": 337000,
          "appliance": null,
          "end_time": 340833,
          "temperature": null,
          "id": 58024,
          "position": 8,
          "display_text": "Enjoy!"
        }
      ],
